import{_ as t,c as e,o,a6 as l}from"./chunks/framework.TqvRFY8P.js";const g=JSON.parse('{"title":"沙拉翻译","titleTemplate":"常见问题","description":"","frontmatter":{"title":"沙拉翻译","titleTemplate":"常见问题"},"headers":[],"relativePath":"docs/faq/howtosociallogin.md","filePath":"docs/faq/howtosociallogin.md"}'),a={name:"docs/faq/howtosociallogin.md"},i=l('<h1 id="如何使用社交账号登录" tabindex="-1">如何使用社交账号登录 <a class="header-anchor" href="#如何使用社交账号登录" aria-label="Permalink to &quot;如何使用社交账号登录&quot;">​</a></h1><p>沙拉翻译从 <code>V4.0.0</code> 版本开始提供在线翻译服务，您可以通过我们的平台直接访问各大主流 AI 提供商和 DeepL 翻译服务。</p><p>如果您是沙拉翻译扩展的用户，并且使用社交账号注册，现在希望在桌面版应用中使用相同的账号登录，或者您就是想通过社交账号登录桌面应用，可以按照以下步骤操作：</p><h2 id="操作步骤" tabindex="-1">操作步骤 <a class="header-anchor" href="#操作步骤" aria-label="Permalink to &quot;操作步骤&quot;">​</a></h2><ol><li><p><strong>访问官网登录</strong></p><ul><li>前往<a href="https://saladict.net/auth/login" target="_blank" rel="noreferrer">沙拉翻译官网</a></li><li>使用您的社交媒体账号（Google、GitHub）登录</li></ul></li><li><p><strong>设置密码</strong></p><ul><li>登录后，访问<a href="https://saladict.net/settings/security" target="_blank" rel="noreferrer">安全设置页面</a></li><li>点击「重置密码」按钮</li><li>查收重置密码邮件，按照邮件提示完成密码设置</li></ul></li><li><p><strong>桌面应用登录</strong></p><ul><li>打开沙拉翻译桌面应用</li><li>使用社交账号关联的邮箱地址和您刚设置的密码进行登录</li></ul></li></ol><p>完成以上步骤后，您就可以在桌面应用中使用与扩展版相同的账号了。</p><blockquote><p><strong>提示</strong>：设置密码后，您既可以继续使用社交账号登录，也可以使用邮箱+密码的方式登录，两种方式完全等效。</p></blockquote>',7),r=[i];function s(n,c,p,_,d,h){return o(),e("div",null,r)}const f=t(a,[["render",s]]);export{g as __pageData,f as default};
