import{_ as a,c as e,o,a6 as t}from"./chunks/framework.TqvRFY8P.js";const u=JSON.parse('{"title":"沙拉翻译","titleTemplate":"常见问题","description":"","frontmatter":{"title":"沙拉翻译","titleTemplate":"常见问题"},"headers":[],"relativePath":"docs/faq/macos15Shortcut.md","filePath":"docs/faq/macos15Shortcut.md"}'),c={name:"docs/faq/macos15Shortcut.md"},r=t('<h1 id="macos-15-快捷键失效" tabindex="-1">macOS 15 快捷键失效 <a class="header-anchor" href="#macos-15-快捷键失效" aria-label="Permalink to &quot;macOS 15 快捷键失效&quot;">​</a></h1><p>macOS 15 系统禁止 <code>应用商店安装的App</code> 使用 <code>option + 字母/数字</code> 作为快捷键，如果您使用的是这些快捷键，目前可通过以下几种方案解决。</p><blockquote><p>这是 Apple 为了安全刻意进行的修改，详情可以查看 <a href="https://support.apple.com/en-us/HT213982" target="_blank" rel="noreferrer">这个链接</a>。</p></blockquote><h2 id="方案-1-升级系统到-macos-15-2-推荐方案" tabindex="-1">方案 1：升级系统到 macOS 15.2（推荐方案） <a class="header-anchor" href="#方案-1-升级系统到-macos-15-2-推荐方案" aria-label="Permalink to &quot;方案 1：升级系统到 macOS 15.2（推荐方案）&quot;">​</a></h2><p>Apple 官方在 macOS 15.2 Beta 2 (Build: 24C5073e) 已经修复了无法使用 <code>Option + 字母</code> 作为快捷键的问题，也就是说现在只有 macOS 15.0 到 macOS 15.2 Beta 2 之间的系统版本的用户存在快捷键失效的问题，目前建议大家升级到 macOS 15.2 系统即可解决问题。</p><h2 id="方案-2-通过修改快捷键解决" tabindex="-1">方案 2：通过修改快捷键解决 <a class="header-anchor" href="#方案-2-通过修改快捷键解决" aria-label="Permalink to &quot;方案 2：通过修改快捷键解决&quot;">​</a></h2><p>如果可以接受不使用 <code>option + 字母</code> 这种快捷键，那将快捷键修改为 <code>control + 字母</code>、<code>command + shift + 字母</code> 之类的其他快捷键即可正常使用。</p>',7),s=[r];function d(l,n,p,m,i,h){return o(),e("div",null,s)}const S=a(c,[["render",d]]);export{u as __pageData,S as default};
