import{_ as e,c as t,o as a,a6 as n}from"./chunks/framework.TqvRFY8P.js";const f=JSON.parse('{"title":"Saladict","titleTemplate":"General Settings","description":"","frontmatter":{"title":"Saladict","titleTemplate":"General Settings"},"headers":[],"relativePath":"en/docs/config/general.md","filePath":"en/docs/config/general.md"}'),i={name:"en/docs/config/general.md"},o=n('<h1 id="general-settings" tabindex="-1">General Settings <a class="header-anchor" href="#general-settings" aria-label="Permalink to &quot;General Settings&quot;">​</a></h1><h2 id="auto-start" tabindex="-1">Auto Start <a class="header-anchor" href="#auto-start" aria-label="Permalink to &quot;Auto Start&quot;">​</a></h2><p>Automatically start Saladict when system boots.</p><blockquote><p>Mac App Store version does not support this feature, but you can manually set it up, which is also very simple. Open the system settings, search for &quot;login items&quot;, find &quot;open at login&quot;, click the bottom left &quot;+&quot; button, add &quot;Saladict&quot;, and you can achieve the startup at login. We will support this feature soon.</p></blockquote><h2 id="hide-icon-in-dock" tabindex="-1">Hide Icon in Dock <a class="header-anchor" href="#hide-icon-in-dock" aria-label="Permalink to &quot;Hide Icon in Dock&quot;">​</a></h2><p>Hide the icon in the bottom Dock bar, including the translation window and settings window.</p><h2 id="show-translation-icon-when-text-is-selected" tabindex="-1">Show Translation Icon When Text is Selected <a class="header-anchor" href="#show-translation-icon-when-text-is-selected" aria-label="Permalink to &quot;Show Translation Icon When Text is Selected&quot;">​</a></h2><p>Show the translation icon when text is selected. Click the translation icon to translate.</p><blockquote><p>This feature is experimental and not supported on Linux and Mac App Store versions. It is recommended to use the <a href="/en/docs/quick-translate.html">Quick Selection Translate</a> feature.</p></blockquote><h2 id="application-language" tabindex="-1">Application Language <a class="header-anchor" href="#application-language" aria-label="Permalink to &quot;Application Language&quot;">​</a></h2><p>Change the application interface display language. To help improve Saladict translations, please visit <a href="https://hosted.weblate.org/engage/saladict-app/" target="_blank" rel="noreferrer">Weblate</a></p><h2 id="application-theme" tabindex="-1">Application Theme <a class="header-anchor" href="#application-theme" aria-label="Permalink to &quot;Application Theme&quot;">​</a></h2><p>Control the application light/dark theme. If <code>Follow System</code> is selected, the interface theme will be consistent with the system settings.</p><p>Note: On Linux systems, it will be consistent with Gtk3 theme. If detection is incorrect, please check your Gtk3 theme settings.</p><h2 id="tray-click-event" tabindex="-1">Tray Click Event <a class="header-anchor" href="#tray-click-event" aria-label="Permalink to &quot;Tray Click Event&quot;">​</a></h2><p>Control the tray icon click event. This feature is not available on Linux.</p><h2 id="transparency-effect" tabindex="-1">Transparency Effect <a class="header-anchor" href="#transparency-effect" aria-label="Permalink to &quot;Transparency Effect&quot;">​</a></h2><p>Control whether the left navigation bar in the settings page is transparent.</p><div class="warning custom-block"><p class="custom-block-title">Note</p><ul><li>Mac does not support this feature</li></ul></div>',19),l=[o];function s(r,c,h,p,d,u){return a(),t("div",null,l)}const g=e(i,[["render",s]]);export{f as __pageData,g as default};
