import{_ as t,c as o,o as e,a6 as a}from"./chunks/framework.TqvRFY8P.js";const f=JSON.parse('{"title":"Saladict","titleTemplate":"FAQ","description":"","frontmatter":{"title":"Saladict","titleTemplate":"FAQ"},"headers":[],"relativePath":"en/docs/faq/howtosociallogin.md","filePath":"en/docs/faq/howtosociallogin.md"}'),i={name:"en/docs/faq/howtosociallogin.md"},s=a('<h1 id="how-to-use-social-account-login" tabindex="-1">How to Use Social Account Login <a class="header-anchor" href="#how-to-use-social-account-login" aria-label="Permalink to &quot;How to Use Social Account Login&quot;">​</a></h1><p>Saladict has been providing online translation services since version <code>V4.0.0</code>. You can directly access major AI providers and DeepL translation services through our platform.</p><p>If you are a Saladict extension user who registered with a social account and now want to use the same account to log in to the desktop application, or if you simply want to log in to the desktop application using a social account, follow these steps:</p><h2 id="operation-steps" tabindex="-1">Operation Steps <a class="header-anchor" href="#operation-steps" aria-label="Permalink to &quot;Operation Steps&quot;">​</a></h2><ol><li><p><strong>Visit Official Website to Login</strong></p><ul><li>Go to <a href="https://saladict.net/auth/login" target="_blank" rel="noreferrer">Saladict Official Website</a></li><li>Log in with your social media account (Google, GitHub)</li></ul></li><li><p><strong>Set Password</strong></p><ul><li>After logging in, visit <a href="https://saladict.net/settings/security" target="_blank" rel="noreferrer">Security Settings Page</a></li><li>Click the &quot;Reset Password&quot; button</li><li>Check your email for the password reset link and follow the instructions to complete password setup</li></ul></li><li><p><strong>Desktop Application Login</strong></p><ul><li>Open Saladict desktop application</li><li>Use the email address associated with your social account and the password you just set to log in</li></ul></li></ol><p>After completing the above steps, you can use the same account in the desktop application as in the extension version.</p><blockquote><p><strong>Note</strong>: After setting a password, you can continue to log in using your social account or use the email + password method. Both methods are completely equivalent.</p></blockquote>',7),n=[s];function l(c,r,p,u,d,h){return e(),o("div",null,n)}const _=t(i,[["render",l]]);export{f as __pageData,_ as default};
